public class Solution {
    public List<String> findWords(char[][] board, String[] words) {
        List<String> result = new ArrayList<String>(words.length);
        if(board == null || words == null) {
            return result;
        }
        
        int R = board.length;
        if(R == 0)  return result;
        int C = board[0].length;
        if(C == 0)  return result;
        
        int numWords = words.length;
        if(numWords == 0)    return result;
        
        int sizeBoard = R*C;
        ArrayList<ArrayList<Integer>> adjList = new ArrayList<ArrayList<Integer>>(sizeBoard);
        
        buildGraph(board, R, C, adjList);
        
        boolean[] visited = new boolean[sizeBoard];
        for(String word:words) {
            for(int i = 0; i < sizeBoard; i++) {
                if(dfs(board, adjList, word, i, 0, visited, C)) {
                    result.add(word);
                    break;
                }
            }
        }
        return result;
        
    }
    
    private boolean dfs(char[][] board, ArrayList<ArrayList<Integer>> adjList, String word, int listIdx, int wordIdx, boolean[] visited, int C) {
        int[] rc = i2rc(listIdx, C);
        int r = rc[0];
        int c = rc[1];
        if(board[r][c] == word.charAt(wordIdx)) {
            wordIdx++;
            if(wordIdx == word.length()){
                return true;
            }
            
            visited[listIdx] = true;
            for(int i:adjList.get(listIdx)){
                if(dfs(board, adjList, word, i, wordIdx, visited, C))  return true;;
            }
            // backtracking
            visited[listIdx] = false;
        }
        return false;
    }
    
    private void buildGraph(char[][] board, int R, int C, ArrayList<ArrayList<Integer>> adjList) {
        for(int r = 0; r < R; r++) {
            for(int c = 0; c < C; c++) {
                ArrayList<Integer> list = new ArrayList<Integer>();
                // left
                if(c > 0) {
                    list.add(rc2i(r, c-1, C));
                }
                // right
                if(c < C - 1) {
                    list.add(rc2i(r, c+1, C));
                }
                // up
                if(r > 0) {
                    list.add(rc2i(r-1, c, C));
                }
                // down
                if(r < R - 1) {
                    list.add(rc2i(r+1, c, C));
                }
                adjList.add(list);
            }
        }
    }
    
    private int rc2i(int r, int c, int C) {
        return r*C + c;
    }
    
    private int[] i2rc(int i, int C) {
        int[] rc = new int[2];
        rc[0] = i/C;
        rc[1] = i%C;
        return rc;
    }
}

