public class Solution {
    public List<Integer> findSubstring(String s, String[] words) {
        List<Integer> res = new ArrayList<Integer>();
        if(s == null || words == null || words.length == 0) {
            return res;
        }
        
        int len = s.length();
        int wordLen = words[0].length();
        int windowLen = words.length * wordLen;
        if(len == 0 || windowLen == 0 || len < windowLen) {
            return res;
        }
        
        HashMap<Character, HashMap<String, Integer>> dict = new HashMap<Character, HashMap<String, Integer>>();
        for(String w:words) {
            char head = w.charAt(0);
            if(dict.containsKey(head)){
                HashMap<String, Integer> map = dict.get(head);
                if(map.containsKey(w)) {
                    map.put(w, map.get(w) + 1);
                }else {
                    map.put(w, 1);
                }
            } else {
                HashMap<String, Integer> map = new HashMap<String, Integer>();
                map.put(w, 1);
                dict.put(head, map);
            }
        }
        
        for(int i = 0; i <= s.length() - windowLen; i++) {
            //System.out.println(i);
            int j = i;
            HashMap<String, Integer> visited = new HashMap<String, Integer>();
            int windowEnd = i + windowLen;
            for(j = i; j < windowEnd; j += wordLen) {
                char head = s.charAt(j);
                if(dict.containsKey(head)){
                    String subStr = s.substring(j, j + wordLen);
                    if(!dict.get(head).containsKey(subStr)){
                        break;
                    }
                    if(visited.containsKey(subStr)){
                        if(visited.get(subStr) + 1 > dict.get(head).get(subStr)){
                            break;
                        } else {
                            visited.put(subStr, visited.get(subStr) + 1);
                        }
                    } else {
                        visited.put(subStr, 1);
                    }
                } else {
                    break;
                }
            }
            //System.out.println(j);
            if(j == windowEnd) {
                res.add(i);
            }
            
        }
        return res;
    }
}

